import React from 'react';
const moment =require('moment')
export const ControllerMessage =({messages})=>{

    const getTime=()=>{
        return moment("20111031", "YYYYMMDD").fromNow();
    }
    return(
        <>
        <ul>
            {
                messages.map(item=>(
                    <li>
                    <p>Single-click color picking - click on the main ColorZilla toolbar icon and
                    <br />
                    <small> {getTime()}</small>
                    </p>
                    </li>
                ))
            }

         </ul>
        </>
    )
}
