import React from 'react';

const moment =require('moment')

export const ControllerMessage =({messages})=>{



    const getTime=(data)=>{
        return moment(data, "YYYYMMDD").fromNow();
    }
    return(
        <>
        <ul>
            {
                messages.map(({message, date},index)=>(
                    <li key={index}>
                    <p>
                        {message}
                    <br />
                    <small> {getTime(date)}</small>
                    </p>
                    </li>
                ))
            }

         </ul>
        </>
    )
}
