import React,{useState,useEffect} from 'react';
import { ControllerHeader } from './components/ControllerHeader';
import { ControllerMessage } from './components/ControllerMessage';
import { ControllerSend } from './components/ControllerSend';
import { getMessages } from './hooks/getMessages';
export const Chat =()=>{
  const [listMessages, setListMessages] = useState([{message: 'Hola', date: new Date()}])
  useEffect(() => {
    const url = "https://majimenezaquino-chat.herokuapp.com/message";

    const fetchData = async () => {
        try {
            const response = await fetch(url);
            const json = await response.json();
            setListMessages(json)
            console.log(json)

        } catch (error) {
            console.log("error", error);
        }
    };

    fetchData();
}, []);

  return (
    <>
      <div className="container-chat">
       <div className="container">
         <div className="header">
          <ControllerHeader />
         </div>
        <div className="container-message">
        <ControllerMessage  messages={listMessages} />
        </div>
        <ControllerSend setMessages={setListMessages} />
       </div>

      </div>
    </>
  )
}
