import React from 'react';
const moment =require('moment')
export const ControllerMessage =({messages})=>{
    const getMessages = async () =>{
        const resp =await fetch("http://localhost:9000/message")
        const data = await resp.json()
        console.log(data)
    }

    getMessages()

    const getTime=()=>{
        return moment("20111031", "YYYYMMDD").fromNow();
    }
    return(
        <>
        <ul>
            {
                messages.map(({message, date},index)=>(
                    <li key={index}>
                    <p>
                        {message}
                    <br />
                    <small> {getTime()}</small>
                    </p>
                    </li>
                ))
            }

         </ul>
        </>
    )
}
